-- Apply VUI theme to Plater elements
function Plater:ApplyVUITheme()
    -- Get current theme
    local theme = VUI.db.profile.core.theme or "thunderstorm"
    
    -- Get theme colors
    local colors = VUI.Media.ThemeColors[theme]
    if not colors then
        VUI:Print("Warning: Could not apply VUI theme to Plater (theme colors not found)")
        return
    end
    
    -- Apply theme colors to Plater's color table
    if Plater and Plater.db and Plater.db.profile then
        -- Apply primary theme color to target highlight
        if Plater.db.profile.target_highlight_color then
            Plater.db.profile.target_highlight_color[1] = colors.primary.r
            Plater.db.profile.target_highlight_color[2] = colors.primary.g
            Plater.db.profile.target_highlight_color[3] = colors.primary.b
        end
        
        -- Apply theme textures
        if Plater.db.profile.health_statusbar_texture then
            Plater.db.profile.health_statusbar_texture = "Interface\\AddOns\\VUI\\media\\statusbar\\VUI_" .. theme
        end
        
        -- Apply border textures
        if Plater.db.profile.border_texture then
            Plater.db.profile.border_texture = "Interface\\AddOns\\VUI\\media\\nameplates\\border"
        end
        
        VUI:Print("Applied VUI " .. theme .. " theme to Plater elements")
    end
end